f;                dir=dir.replace('/<?php echo $lan;?>/' ,'/'+ lan.toLowerCase() + '/' );                document.location.href=dir;            }            function closeMenus() {                window.clearTimeout(timeoutId);                $$('.submenu_actions').each(function(element){                    element.hide();            	});            }            function showMenu(event) {                var element = event.element();                var parentElement = element.up('.submenu_trigger');                if (parentElement) {                    elementToShow = parentElement.down('ul');                    if ((elementToShow)&&(!elementToShow.visible())) {                        closeMenus();                        timeoutId = window.setTimeout("Effect.Appear(elementToShow, {duration:0.5})",500);                    }                }            }            function hideMenu(event) {                var toElement = event.relatedTarget || event.toElement;                if (toElement) {                    var testElement = toElement.up('#menu');                    if (testElement)                        return false;                    closeMenus();                }            }            function initMenu() {                var element = $('menu');                Event.observe(element,'mouseover',showMenu);                Event.observe(element,'mouseout',hideMenu);            }            function toggleHelp() {                var help = $('help');                help.toggle();                if (help.visible()) {                    new Ajax.Updater(help, '<?php echo html::url($helpUrl); ?>' , { method: 'get', evalScripts: true});                }            }            Event.observe(window, 'load', initMenu);    </script>  </head>  <body>  	<div id='container'>        <?php include "header.php"; ?>  	    <div id='menu'>            <div id='menu_top'></div>            <div id='menu_actions'>                <ul class='menu_content'>                    <?php echo $actions_menu ?>                </ul>            </div>            <div id='menu_user'>                <?php echo $user_menu ?>            </div>            <div id='menu_bottom'></div>        </div>    	<div id='content' class='content' >            <div id='content_top'></div>			<?php echo $content ?>            <div id='content_bottom'></div>		</div>      <?php include "footer.php"; ?>    </div>  <?php include "credentials.php"; ?>  </body></html>